import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';

function AdminLogin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    if (username === 'admin' && password === 'password') {
      setIsLoggedIn(true);
    } else {
      setError('Invalid username or password');
    }
  };

  if (isLoggedIn) {
    return <Navigate to="/dashboard" />;
  }

  return (
    <>
    <h1 style={{    color: '#1F253F',
    textAlign: 'center',
    marginTop: '80px'}}>Online Service Finder</h1>
    <h3 style={{    color: '#1F253F',
    textAlign: 'center',
    marginTop: '30px'}}>Admin Login</h3>
    <form style={{
        textAlign: 'center',
        margin: '2% 36%', border: '2px solid', paddingTop: '30px', paddingBottom: '30px'
    }} onSubmit={handleSubmit}>
      {error && <p style={{color:'red'}}>{error}</p>}
      <label>
        USERNAME : 
        <input style={{padding:'5px', margin: '10px'}}
          type="text"
          value={username}
          onChange={(event) => setUsername(event.target.value)}
        />
      </label>
      <br />
      <label>
        PASSWORD : 
        <input style={{padding:'5px', margin: '10px'}}
          type="password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />
      </label>
      <br />
      <button style={{ margin: '10px'}}  type="submit">login</button>
      <br />
      <a href="/forgot-password" style={{color: 'black', marginBottom:'10px'}}>Forgot password?</a>
    </form>
    </>
  );
}

export default AdminLogin;
