import React, { useState }  from 'react';
import './worker-profile.css'
import Navbar from "./../../components/navbar/Navbar";
import Breadcrumb from "../../components/breadcrumb/Breadcrumb";
import Footer from "../../components/footer/Footer";
import { useLocation } from 'react-router-dom'
import { createOrder } from '../../actions/orders';

const WorkerProfile = () =>{
  const location = useLocation()
  const { post } = location.state
  const dispatch = useDispatch();

  const [orderData, setOrderData] = useState({
    firstName: post.firstName,
    lastName:  post.lastName,
    work:  post.work,
    contact:  post.lastName,
    time: '',
    date: '',
    address

});
  const handleSubmit = async (e) => {
    e.preventDefault();
    dispatch(createOrder(orderData))
    try {
      // Send date and time input data to the server
      const response = await fetch('/api/saveDateTime', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ date, time })
      });

      if (response.ok) {
        alert('Date and time input saved successfully!');
      } else {
        alert('Failed to save date and time input.');
      }
    } catch (error) {
      console.error(error);
    }
  };



  return (
    <>
<Navbar />
       <Breadcrumb title="Worker Profile" />
       <div className='main-div'>
        <div className='main-div-div-1'>
        <img src='www.abc.com/img.png'></img>
        <p>Availability: </p>
        <p>Total Expereince:</p>
        <p>Reviews: </p>
        <p>Contact No: {post.contact}</p>
        <p>CNIC: {post.cnic}</p>
        </div>
        <div className='main-div-div-2'>
        <p>Name: {post.firstName} {post.lastName}</p>
        <p>Service: {post.work}</p>
        <p>About: {post.description}</p>
        <p>Address: </p> 
        <p>Expecting Salary /month: {post.salary}</p>
        </div>
       </div>

      <form onSubmit={handleSubmit}> 
      <div className="username" style={{margin: '10px 0px'}}>
                    <label className="form__label" for="firstName">Enter your Address: </label>
                    <input style={{padding:'5px', marginLeft:'20px'}} 
                    className="form__input" 
                    type="text" 
                    name="address"
                    id="address" 
                    placeholder="Address"
                    value={postData.firstName}
                    onChange={(e) => setOrderData( {...setOrderData, firstName: e.target.value })}
                    />
                    {/* <p style={{fontSize: '14px',color: 'red',marginTop: '5px',marginBottom: '5px'}}>{error.firstName}</p> */}
                </div>
        <label>
        Date:
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
      </label>
      <br />
      <label>
        Time:
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
      </label>
      <br />

       <button style={{marginLeft:'10px'}} type="submit" class="btn">Book</button>
      </form>
       


    {/* // <div>
    //   <h2>{name}</h2>
    //   <img src={image} alt={name} />
    //   <p>Service: {service}</p>
    //   <p>Salary: {salary}</p>
    //   <p>Description: {description}</p>
    //   <p>Area: {area}</p>
    // </div> */}
    <Footer />
    </>
  );
}

export default WorkerProfile;
