import Post from './workerPost';
import Worker from './finalWorkers';
import { useSelector } from 'react-redux';
import { useDispatch} from 'react-redux';

const Posts = ({setCurrentPostId, name, purpose, currentPostId, currentId, setCurrentId})=> {

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(fetchApprovedPosts());
    }, [currentId, dispatch]);

    const posts = useSelector((state) => state.posts);
    const approvedPosts = useSelector((state) => state.approvedPosts);
    const state = useSelector((state) => state);
    console.log(state)
    return(
        !posts.length ?null: (
        <div >
                    {
                        posts.map((post) => (
                            
                            <div key={post._id}>
                                 {purpose ? (<Worker currentPostId={currentPostId} setCurrentPostId={setCurrentPostId} post = {post}></Worker>):  ( <Post post = {post} name = {name} currentId = {currentId} setCurrentId = {setCurrentId}></Post>)}
                           
                            
                            </div>
                        ))
                    }
        </div>)
    )
}

export default Posts;
