import './WorkerCard.css'
import "./workerPost.css"
import {Link} from 'react-router-dom';

const Post = ({post, setCurrentId})=> {
    const user = JSON.parse(localStorage.getItem('profile'));

    const handleClick = () => {
        if (user?.result?.name){
            document.getElementById("dn").classList.add("dn")
            document.getElementById("dv").classList.remove("dv")
        }else{
            document.getElementById("hide").classList.remove("hide")
            document.getElementById("dn").classList.add("dn")
        }
    }
    
    return(
        <div className='worker-card'>
            <div className="cards">
                <div className="card">
                    {/* <img src={props.imgsrc} alt="Service-Pic" className="card__img"></img> */}
                    <div className="card__info">
                        <h3 className="card__title">{post.work}</h3>
                        <h3 className="card__title">{post.firstName}{post.lastName}</h3>
                        <h3 className="card__title">Starting From: {post.salary}</h3>
                        <p>{post.description}</p>
                        <p className='dv' id = "dv">{post.contact}</p>
                        <a href={post.link}>
                            <button onClick = {handleClick} id = "dn" className='service-btn '>Hire Now</button>
                            <Link to="/auth" ><button id = "hide" className='service-btn hide'>SignIn</button></Link>
                        </a>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Post;
