import "./table.css"
import { useDispatch} from 'react-redux';
import { getOrders, deleteOrder} from '../../actions/orders';
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';

const TableAppoinment = () => {
    const dispatch = useDispatch();
    const [user] = useState(JSON.parse(localStorage.getItem('profile')));
  
    useEffect(() => {
        dispatch(fetchApprovedPosts());
      }, [dispatch]);

    const orders = useSelector((state) => state.orders);
      // console.log(orders)
  
    return (
      <div>
        {
            orders.map((order) => (                            
                <div key={order._id}>
                    <h3 >{order.userName}</h3>
                    <p >Schedule: {order.date.slice(0, 10)} {order.time}</p>
                    <p >Mob: {order.userContact}</p>
                    <p >Order No: {order._id}</p>
                    <p >Status:</p>
                    <button onClick={() => {
                      dispatch(deleteOrder(order._id))
                      window.location.reload();
                      }}>Cancel Order</button>
                    <br></br>
                </div>
            ))
        }
      </div>
    )
  }

export default TableAppoinment;
