import React, {useState } from 'react';
import { useDispatch} from 'react-redux';
import { workerSignin} from '../../actions/auth';
import { useNavigate } from 'react-router-dom';
  
function WorkerLogin(){
    const dispatch = useDispatch();
    const history = useNavigate();

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const form = [username, password];
    const [error, setErrors] = useState({});
  
    const handleSubmit = (event) => {
      event.preventDefault();
      var errors = validate(form)
      setErrors(validate(form));
      if(Object.keys(errors).length === 0 && errors.constructor === Object){
        dispatch(workerSignin(form, history));
      }
      

    };

    const validate = (values) => {
      const errors = {}
      const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
      if (!values.firstName){
          errors.firstName = "First Name is required!"
      }
      if (!values.lastName){
          errors.lastName = "Last Name is required!"
      }

      if (!values.password){
        errors.password = "Password is required!"
    }
      return errors
  }

    return (
      <>
      <h1 style={{    color: '#1F253F',
      textAlign: 'center',
      marginTop: '80px'}}>Online Service Finder</h1>
      <h3 style={{    color: '#1F253F',
      textAlign: 'center',
      marginTop: '30px'}}>Worker Login</h3>
      <form style={{
          textAlign: 'center',
          margin: '2% 36%', border: '2px solid', paddingTop: '30px', paddingBottom: '30px'
      }} onSubmit={handleSubmit}>
        {error && <p style={{color:'red'}}>{error}</p>}
        <label>
          USERNAME : 
          <input style={{padding:'5px', margin: '10px'}}
            type="text"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
            <p style={{fontSize: '14px',color: 'red',marginTop: '5px',marginBottom: '5px'}}>{error.username}</p>
               
        </label>
        <br />
        <label>
          PASSWORD : 
          <input style={{padding:'5px', margin: '10px'}}
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
            <p style={{fontSize: '14px',color: 'red',marginTop: '5px',marginBottom: '5px'}}>{error.password}</p>
               
        </label>
        <br />
        <button style={{ margin: '10px'}}  type="submit">login</button>
        <br />
        <a href="/forgot-password" style={{color: 'black', marginBottom:'10px'}}>Forgot password?</a>
      </form>
      </>
    );
}

export default WorkerLogin;
