import { useDispatch} from 'react-redux';
import { getOrders } from '../../actions/orders';
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';

const Order = () => {
  const dispatch = useDispatch();
  const [user] = useState(JSON.parse(localStorage.getItem('profile')));

  useEffect(() => {
        dispatch(getOrders(user.result._id));
    }, [dispatch]);

  const orders = useSelector((state) => state.orders);
    // console.log(orders)
  return (
    <div>
      {
          orders.map((order) => (                            
              <div key={order._id}>
                  <h3 className="wcard__title">{order.work}</h3>
                  <h3 className="wcard__name">{order.firstName} {order.lastName}</h3>
                  <h3 className="wcard__title">Time Schedule: {order.date.slice(0, 10)} {order.time}</h3>
                  <p className='dv' id = "dv">Mob: {order.contact}</p>
                  <p className='dv' id = "dv">Mob: {order.contact}</p>
              </div>
          ))
      }
    </div>
  )
}

export default Order;
